mapper.select:
SELECT i.coach_id,i.coach_desc,i.coach_name,i.coach_price,m.image_url
FROM tb_coach_info i, tb_coach_image m
where i.coach_id = m.coach_id AND i.coach_id = #{id}

SELECT image_id,image_url
FROM tb_coach_image
where coach_id = #{id}

SELECT i.coach_id,b.blog_content,m.image_url
FROM tb_coach_info i,tb_user_blog b,tb_user_blog_image m
where i.user_id = b.user_id AND b.blog_id = m.blog_id AND i.coach_id = #{id}

SELECT i.coach_id,i.coach_desc,i.coach_name,i.coach_price,m.image_url
FROM tb_coach_info i, tb_coach_image m

    i.coach_id = m.coach_id

        and i.tag_id = #{tagId}


        order by i.coach_price


        order by i.coach_price desc


        and i.tag_id = #{tagId}
        order by i.coach_price


        and i.tag_id = #{tagId}
        order by i.coach_price desc
